mapper.insert:
insert into app (appName, actionClass, appPath, 
  userid, created, lastUpdated
  )
values (#{appName,jdbcType=VARCHAR}, #{actionClass,jdbcType=VARCHAR}, #{appPath,jdbcType=VARCHAR}, 
  #{userid,jdbcType=INTEGER}, #{created,jdbcType=DATE}, #{lastUpdated,jdbcType=DATE}
  )

mapper.select:
select id, appName, actionClass, appPath, userid, created, lastUpdated
from app
where id = #{id,jdbcType=INTEGER}
